$(document).ready(function(){$.ajax({method:"GET",url:"@Url.RouteUrl(MvcRoutes.StatesListAllRouteName)",dataType:"json",success:function(n){$.each(n,function(n,t){$(" #addPersonModal #StateId").append($("<option value='"+t.StateId+"'>"+t.StateCode+" <\/option>"));$(" #editPersonModal #StateId").append($("<option value='"+t.StateId+"'>"+t.StateCode+" <\/option>"))})},error:function(n,t,i){$("#divPersonModalAlert").show();$("#divPersonModalAlert").html(i)}});$(".btn-edit").click(function(){$("#editPersonModal").modal("show");$.ajax({method:"GET",url:"@Url.RouteUrl(MvcRoutes.PersonGetByIdRouteName)",dataType:"json",data:{id:$(this).attr("data-id")},success:function(n){var t=moment(n.DateOfBirth).format("YYYY-MM-DD");$(" #formEditPerson #PersonId").val(n.PersonId);$(" #formEditPerson #LastName").val(n.LastName);$(" #formEditPerson #FirstName").val(n.FirstName);$(" #formEditPerson #Gender").val(n.Gender);$(" #formEditPerson #DateOfBirth").val(t);$(" #formEditPerson #StateId").val(n.StateId)},error:function(n,t,i){$("#divPersonModalAlert").show();$("#divPersonModalAlert").html(i)}})});$("#formSearch").submit(function(n){n.preventDefault();$.ajax({method:"GET",url:"@Url.RouteUrl(MvcRoutes.PeopleListRouteName)",dataType:"json",data:{firstName:$("#txtSearchFirstName").val(),lastName:$("#txtSearchLastName").val()},success:function(n){$("#tbodyPeople").html("");$.each(n,function(n,t){var i=moment(t.DateOfBirth).format("YYYY-MM-DD");$("#tbodyPeople").append("<tr><td scope='row'>"+t.LastName+"<\/td><td scope='row'>"+t.FirstName+"<\/td><td scope='row'>"+t.Gender+"<\/td><td scope='row'>"+i+"<\/td><td scope='row'>"+t.StateCode+"<\/td><td><div class='container-fluid'><button class='btn btn-info btn-edit' type='button' data-id='"+t.PersonId+"'><span class='fa fa-edit'><\/span>&nbsp;Edit<\/button>&nbsp;<\/div><\/td><\/tr>")})},error:function(n,t,i){$("#divPersonModalAlert").show();$("#divPersonModalAlert").html(i)}})})});